package java_prg;

public class OddorEven_7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=56;
		switch(n%2) {
		case 0:
			System.out.println("the given number "+n+" is even");
			break;
		case 1:
			System.out.println("the given number "+n+" is odd");
		}

	}

}
